        public bool Connected
        {
            get
            {
                LogMessage("Connected", "Get {0}", IsConnected);
                return IsConnected;
            }
            set
            {
                tl.LogMessage("Connected", "Set {0}", value);
                if (value == IsConnected)
                    return;

                if (value)
                {
                    // Perform a probe request and test for successful connection to the server
                    var req_task = client.GetAsync(lastServer);
                    try
                    {
                        req_task.Wait();
                    }
                    catch (AggregateException e)
                    {
                        foreach(Exception ie in e.InnerExceptions)
                        {
                            LogMessage("HTTP Connection", $"{ie.GetType()} exception: {ie.Message}");
                        }
                        return;
                    }
                    
                    HttpResponseMessage response = req_task.Result;

                    if(!response.IsSuccessStatusCode)
                    {
                        LogMessage("HTTP Connection", $"Probe request return code: ${response.StatusCode}. Body: {response.Content}");
                        return;
                    }

                    connectedState = true;
                    LogMessage("Connected Set", $"Connected to {lastServer}");
                }
                else
                {
                    connectedState = false;
                    LogMessage("Connected Set", $"Disconnecting from {lastServer}");
                }
            }
        }

